import React from 'react';

class QuestionCard extends React.Component {

    trueOrFalse = (answer) => {
      if (props.question.answer === answer) {
        props.adjustPoints(10)
      } else {
        props.adjustPoints(-10)
      }
    }

    render() {
      return (
        <div className="question-card">
            <h2> {props.question.text} </h2>
            <button className="true-button" onClick={(event) => this.trueOrFalse(true, event)}>True</button>
            <button className="false-button" onClick={(event) => this.trueOrFalse(false)}>False</button>
        </div>
      );
    }

  }

  export default QuestionCard;
