import React from 'react';

function QuestionCard(props) {

    const trueAnswer = (answer) => {
      if (props.question.answer === answer) {
        adjustPoints(10)
      } else {
        adjustPoints(-10)
      }
    }

    const falseAnswer = () => {

    }

    return (
      <div className="question-card">
          <h2> {props.question.text} </h2>
          <button className="true-button" onClick={trueAnswer}>True</button>
          <button className="false-button" onClick={falseAnswer}>False</button>
      </div>
    );
  }

  export default QuestionCard;
