import React from 'react';
import LutriviaContainer from './components/LutriviaContainer';
import './App.css';

import data from './data'

class App extends React.Component {

  state = {
    questions: data,
    score: 0,
    answered: false
  }

adjustPoints = (points, questionText) => {
    let updatedQuestions = this.state.questions.questions.map (question => {
      return question.text === questionText ? { ...question, answered: true } : question
    })

    this.setState ({
      questions: {
        questions: updatedQuestions},
      score: this.state.score + points
    })
    debugger
}

  render() {
    return (
      <div>
        <LutriviaContainer questions={this.state.questions.questions} score={this.state.score} adjustPoints={this.adjustPoints} answered={this.state.answered}/>
      </div>
    );
  }

}

export default App;
